import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class EditExercise extends Component {
  constructor(props) {
    super(props);

    this.onChangename = this.onChangename.bind(this);
    this.onChangephoto = this.onChangephoto.bind(this);
    this.onChangenim = this.onChangenim.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
        name: '',
        photo: [],
        photoName: '',
        nim: 0,
        date: new Date(),
    }
  }

  componentDidMount() {
    axios.get('http://localhost:9000/exercise/'+this.props.match.params.id)
      .then(response => {
        this.setState({
          name: response.data.name,
          photoName: response.data.photo,
          nim: response.data.nim,
          date: new Date(response.data.date)
        })   
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  onChangename(e) {
    this.setState({
      name: e.target.value
    })
  }

  onChangephoto(e) {
    this.setState({
      photo: e.target.value
    })
  }

  onChangenim(e) {
    this.setState({
      nim: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    })
  }

  onSubmit(e) {
    e.preventDefault();
    if(this.state.photo){
        const formData = new FormData();
        formData.append('photo',this.state.photo);
    
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };
        //upload foto
        axios.post("http://localhost:9000/exercise/file",formData,config)
            .then((response) => {
                console.log(response);
                this.setState({
                   photoName: response.data.originalname
               })

               const exercise = {
                name: this.state.name,
                photo: this.state.photoName,
                nim: this.state.nim,
                date: this.state.date
               }
          
              console.log(exercise);
          
              axios.post('http://localhost:9000/exercise/update/' + this.props.match.params.id, exercise)
                .then(res => console.log(res.data));        
            })
            .catch((error) => {
                alert(error)
        });   

    }
    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Edit Exercise Log</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group">
            <label>NIM: </label>
            <input 
                type="text" 
                className="form-control"
                value={this.state.nim}
                onChange={this.onChangenim}
            />
        </div>
        <div className="form-group"> 
          <label>Name: </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.name}
              onChange={this.onChangename}
          />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>
        <div className="form-group"> 
          <label>Photo: </label>
          <input  type="file"
              className="form-control"
              name="photo"
              onChange={this.onChangephoto}
              />
        </div>

        <div className="form-group">
          <input type="submit" value="Edit Exercise Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
