import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

export default class CreateExercise extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePhoto = this.onChangePhoto.bind(this);
    this.onChangeNim = this.onChangeNim.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.uploadPhoto = this.uploadPhoto.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      name: '',
      photo: [],
      photoName: '',
      nim: 0,
      date: new Date(),
    }
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value
    })
  }

  onChangePhoto(e) {
    this.setState({
        photo: e.target.files[0],
        photoName: this.state.photo.originalname
    })
  }

  onChangeNim(e) {
    this.setState({
      nim: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    })
  }

  uploadPhoto(){
     const formData = new FormData();
     formData.append('photo',this.state.photo);
 
     const config = {
         headers: {
             'content-type': 'multipart/form-data'
         }
     };
 
     axios.post("http://localhost:9000/exercise/file",formData,config)
         .then((response) => {
             console.log(response);
             this.setState({
                photo: e.target.files[0],
                photoName: this.state.photo.originalname
            })
         }).catch((error) => {
             alert(error)
     });
  }

  onSubmit(e) {
    e.preventDefault();
    this.uploadPhoto();

    // alert(this.state.photoName)

    // const exercise = {
    //   nim: this.state.nim,
    //   name: this.state.name,
    //   date: this.state.date,
    //   photo: this.state.photoName,
    // }

    // console.log(exercise);

    // axios.post('http://localhost:9000/exercise/add', exercise)
    //   .then(res => console.log(res.data));

    //window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Create New Student Data</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group">
          <label>NIM </label>
          <input 
              type="number" 
              className="form-control"
              value={this.state.Nim}
              onChange={this.onChangeNim}
              />
        </div>
        <div className="form-group"> 
          <label>Name: </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.name}
              onChange={this.onChangeName}
          />
        </div>
        <div className="form-group">
          <label>Date of birth: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>
        <div className="form-group"> 
          <label>Photo: </label>
          <input  type="file"
              required
              name="photo"
              className="form-control"
              onChange={this.onChangePhoto}
              />
        </div>
        <div className="form-group">
          <input type="submit" value="Create Exercise Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
